import { useEffect, useState } from "react";

function LikeButton({ likes, myid, onCardLike }) {
    const [isLike, setIsLike] = useState(false)
    const [count, setCount] = useState(likes.length)
    
    useEffect(() => {
        setIsLike(likes.some(element => myid === element._id))
    }, [likes, myid])

    return(
        <>
        <button className={`element__heart ${isLike ? 'element__heart_active' : ''}`} type="button" aria-label="Нравиться" onClick={() => onCardLike(likes)} />
        <p className="element__number">{count}</p>
        </>
    )
}

export default LikeButton;
